mapper.select:
SELECT refresh_token_id, user_id, token, expires_at, created_at, device_info, device_type, ip_address, last_used_at
FROM refresh_tokens
WHERE token = #{token}

SELECT refresh_token_id, user_id, token, expires_at, created_at, device_info, device_type, ip_address, last_used_at
FROM refresh_tokens
WHERE user_id = #{userId}

mapper.update:
UPDATE refresh_tokens
SET last_used_at = #{lastUsedAt}
WHERE token = #{token}
