mapper.select:
SELECT * FROM users 
WHERE email = #{email} 
AND is_deleted = false

SELECT * FROM users 
WHERE user_name = #{username} 
AND is_deleted = false

mapper.update:
UPDATE users
user_name = #{username}, email = #{email}, provider = #{provider}, google_id = #{googleId}, profile_image_url = #{profileImageUrl}, is_email_verified = #{isEmailVerified}
WHERE user_id = #{user_id}
